import React, { useState, useEffect } from 'react';

function CabList() {
    const [cabs, setCabs] = useState([]);
    const [showContent, setShowContent] = useState(false); // Add a state for controlling content visibility
    
    useEffect(() => {
        // Fetch available cabs from your backend
        fetch('http://localhost:5000/api/cab/getAllCabStatus')
            .then((response) => response.json())
            .then((data) => setCabs(data.CabStatus))
            .catch((error) => console.error('Error fetching available cabs:', error));
    }, []);

    const showContentOnClick = () => {
        setShowContent(true); // Set showContent to true when the button is clicked
    };

    return (
        <div className="cab-list">
            <h2>Available Cabs</h2>
            <button onClick={showContentOnClick}>Book Now</button>
            {showContent && ( // Render the content only if showContent is true
                <div className="cabs">
                    <button
                        className="close"
                        onClick={() => {
                            setShowContent(false); // Close the content
                        }}
                    >
                        X
                    </button>
                    <ul>
                        {cabs.map((cab, index) => (
                            <li key={index}>
                                Cab name: {cab.name} - Status: {cab.status} - Rate: {cab.rate}
                            </li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
}

export default CabList;
