import React, { useState } from 'react';

function BookingForm() {
    const [email, setEmail] = useState('');
    const [source, setSource] = useState('');
    const [destination, setDestination] = useState('');

    const handleBooking = async () => {
        try {
            // Send a POST request to your backend to book a cab
            const response = await fetch('/api/bookCab', { // Update the API endpoint
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ email, source, destination }),
            });

            if (response.ok) {
                // Handle success - display the shortest path, total time, and fare
                const data = await response.json();
                console.log('Shortest Path:', data.shortestPath);
                console.log('Total Time:', data.totalTime);
                console.log('Fare:', data.fare);
            } else {
                // Handle error
                console.error('Booking failed.');
            }
        } catch (error) {
            console.error('An error occurred:', error);
        }
    }

    return (
        <div className="booking-form">
            <h2>Book a Cab</h2>
            <label htmlFor="email">Email:</label>
            <input
                type="email"
                id="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="Email"
            />
            <label htmlFor="source">Source:</label>
            <input
                type="text"
                id="source"
                value={source}
                onChange={(e) => setSource(e.target.value)}
                placeholder="Source"
            />
            <label htmlFor="destination">Destination:</label>
            <input
                type="text"
                id="destination"
                value={destination}
                onChange={(e) => setDestination(e.target.value)}
                placeholder="Destination"
            />
            <button onClick={handleBooking}>Book Now</button>
        </div>
    );
}

export default BookingForm;
