import React, { useState } from 'react';
import './bookingform.css';
import CabList from './cablist';

function BookingForm() {
    const [email, setEmail] = useState('');
    const [source, setSource] = useState('');
    const [destination, setDestination] = useState('');
    const [cabs, setCabs] = useState([]);
    const [selectedCab, setSelectedCab] = useState(null);
    const [getShortestRoute, setShortestRoute] = useState('');

    


    const handleBooking = async () => {
        if (!selectedCab) {
           alert('Please select a cab before booking!.');
        }

        try {
            // Send a POST request to your backend to book a cab
            const response = await fetch(`${process.env.REACT_APP_API_BASE_URL}/cab/bookCab`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: { email, source, destination, vehicleNumber: selectedCab.vehicleNumber },
            });

            if (response.ok) {
                // Handle success - display a success message or redirect to a confirmation page
                console.log('Booking successful.');
            } else {
                // Handle error
                console.error('Booking failed.');
            }
        } catch (error) {
            console.error('An error occurred:', error);
        }
    }

    const handleSearchCabs = async () => {
        try {
            const routeResponse = await fetch(`${process.env.REACT_APP_API_BASE_URL}/cab/getShortestRoute?src=${source}&dst=${destination}`);
            const routeData = await routeResponse.json();
            setShortestRoute(routeData.shortestRoute);
            const response = await fetch(`${process.env.REACT_APP_API_BASE_URL}/cab/getAllCabStatus`);
            if (response.ok) {
                const data = await response.json();
                console.log(data);
                setCabs(data.CabStatus);
            } else {
                console.error('Cab search failed.');
            }
        } catch (error) {
            console.error('An error occurred:', error);
        }
    }


    return (
        <div className="booking-form">
            <h2>Book a Cab</h2>
            <label htmlFor="source">Source:</label>
            <input
                type="text"
                id="source"
                value={source}
                onChange={(e) => setSource(e.target.value)}
                placeholder="Source"
            />
            <label htmlFor="destination">Destination:</label>
            <input
                type="text"
                id="destination"
                value={destination}
                onChange={(e) => setDestination(e.target.value)}
                placeholder="Destination"
            />
            <button onClick={handleSearchCabs}>Search for Cabs</button>
            
            {cabs.length > 0 && (
                <div>
                    <h3>Available Cabs</h3>
                        {cabs.sort((a, b) => a.name > b.name).map((cab, index) => (
                            <button className='cab-button' key={index + 1}>
                                <div className='cab-container'>
                                    <h3>Cab Name: {cab?.name}</h3>
                                    <h3>Vehicle Number: {cab?.vehicleNumber}</h3>
                                    <p>Rate per hour: {cab?.rate}</p>
                                    <p>Time: {getShortestRoute.totalTime}</p>
                                    <p>Total price: {getShortestRoute.totalTime * cab.rate}</p>
                                </div>
                            </button>
                        ))}
                </div>
            )}
            
            <button onClick={handleBooking}>Book Now</button>
        </div>
    );
}

export default BookingForm;
