import React, { useState, useEffect } from 'react';

function CabList() {
    const [cabs, setCabs] = useState([]);
    const [show, setShow] = useState(false);

    const clickHandler = async () => {
        try {
            // Fetch available cabs from your backend
            const response = await fetch('/api/available-cabs');
            if (response.ok) {
                const data = await response.json();
                setCabs(data.CabStatus);
                setShow(true);
            } else {
                console.error('Error fetching available cabs:', response.status);
            }
        } catch (error) {
            console.error('An error occurred:', error);
        }
    }

    return (
        <div className="cab-list">
            <h2>Available Cabs</h2>
            <button onClick={clickHandler}>Search</button>
            {show && (
                <div className="cabs">
                    <button
                        className="close"
                        onClick={() => {
                            setShow(false);
                        }}
                    >
                        X
                    </button>
                    {cabs.map((cab, index) => (
                        <div key={index}>
                            <p>Cab name: {cab.name}</p>
                            <p>Price: {cab.rate}</p>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}

export default CabList;
