import React, { useState } from 'react';
import './bookingform.css';
import CabList from './cablist';

function BookingForm() {
    const [email, setEmail] = useState('');
    const [source, setSource] = useState('');
    const [destination, setDestination] = useState('');
    const [cabs, setCabs] = useState([]);
    const [selectedCab, setSelectedCab] = useState(null);

    


    const handleBooking = async () => {
        if (!selectedCab) {
           alert('Please select a cab before booking!.');
        }

        try {
            // Send a POST request to your backend to book a cab
            const response = await fetch(`${process.env.REACT_APP_API_BASE_URL}/cab/bookCab`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ email, source, destination, vehicleNumber: selectedCab.vehicleNumber }),
            });

            if (response.ok) {
                // Handle success - display a success message or redirect to a confirmation page
                console.log('Booking successful.');
            } else {
                // Handle error
                console.error('Booking failed.');
            }
        } catch (error) {
            console.error('An error occurred:', error);
        }
    }

    const handleSearchCabs = async () => {
        try {

            console.log(source, destination);
            const response = await fetch(`${process.env.REACT_APP_API_BASE_URL}/cab/getShortestRoute?src=${source}&dst=${destination}`);
            if (response.ok) {
                const data = await response.json();
                console.log(data);
                setCabs(data.CabStatus);
            } else {
                console.error('Cab search failed.');
            }
        } catch (error) {
            console.error('An error occurred:', error);
        }
    }


    return (
        <div className="booking-form">
            <h2>Book a Cab</h2>
            {/* <label htmlFor="email">Email:</label>
            <input
                type="email"
                id="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="Email"
            /> */}
            <label htmlFor="source">Source:</label>
            <input
                type="text"
                id="source"
                value={source}
                onChange={(e) => setSource(e.target.value)}
                placeholder="Source"
            />
            <label htmlFor="destination">Destination:</label>
            <input
                type="text"
                id="destination"
                value={destination}
                onChange={(e) => setDestination(e.target.value)}
                placeholder="Destination"
            />
            <button onClick={handleSearchCabs}>Search for Cabs</button>
            
            {cabs.length > 0 && (
                <div>
                    <h3>Available Cabs</h3>
                    <ul>
                        {cabs.map((cab, index) => (
                            <li key={index}>
                                {cab.name} - Rate per minute: {cab.rate}
                                <button onClick={() => setSelectedCab(cab)}>Select</button>
                            </li>
                        ))}
                    </ul>
                </div>
            )}
            
            <button onClick={handleBooking}>Book Now</button>
        </div>
    );
}

export default BookingForm;
